var backtop = angular.module('angular.backtop', []);

backtop.directive('backTop', [function() {
  return {
    restrict: 'E',
    transclude: true,
    replace: true,
    template: '<a id="backtop" href="javascript:void(0);" style="font-size: 9px;margin: 5px 10px;" class="{{theme}}"><i class="fa fa-chevron-up fa-2x" aria-hidden="true" style="display: block;"></i> {{text}}</a>',
    scope: {
      text: "@buttonText",
      speed: "@scrollSpeed",
			theme: "@buttonTheme"
    },
    link: function(scope, element) {

      scope.text = scope.text || 'Go top!';
      scope.speed = parseInt(scope.speed, 10) || 300;
      scope.theme = (scope.theme || '');

      var self = this;

      scope.currentYPosition = function() {
        if (self.pageYOffset)
          return self.pageYOffset;
        if (document.documentElement && document.documentElement.scrollTop)
          return document.documentElement.scrollTop;
        if (document.body.scrollTop)
          return document.body.scrollTop;
        return 0;
      };

      scope.smoothScroll = function() {
        var startY = scope.currentYPosition();
        var stopY = 0;
        var distance = stopY > startY ? stopY - startY : startY - stopY;
        if (distance < 100) {
          scrollTo(0, stopY);
          return;
        }
        var speed = Math.round(scope.speed / 100);
        var step = Math.round(distance / 25);
        var leapY = stopY > startY ? startY + step : startY - step;
        var timer = 0;
        if (stopY > startY) {
          for (var i = startY; i < stopY; i += step) {
            setTimeout("window.scrollTo(0, " + leapY + ")", timer * speed);
            leapY += step;
            if (leapY > stopY) leapY = stopY;
            timer++;
          }
          return;
        }
        for (var j = startY; j > stopY; j -= step) {
          setTimeout("window.scrollTo(0, " + leapY + ")", timer * speed);
          leapY -= step;
          if (leapY < stopY) leapY = stopY;
          timer++;
        }
      };

      scope.a = element;
			element.addClass('hidden');

      scope.a.on('click', function() {
        scope.smoothScroll();
        element.addClass('hidden');
      });

      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 0) {
          element.removeClass('hidden');
        } else {
          element.addClass('hidden');
        }
      });
    }
  };

}]);
